sections.css:
@import '../reset';
@import '../theme_vars';

.Theme {
  @include font-properties(bodyText, base);
  background-color: $Theme-color-white;
  color: $Theme-palette-text-body-default;

  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
  font-weight: $Theme-typography-fontWeight-regular;
  text-rendering: optimizeLegibility;
}

@import '../reset';
@import '../theme_vars';

$ButtonPrimary-default-background: $Theme-color-yellowOrange;
$ButtonPrimary-default-color: $Theme-color-white;
$ButtonPrimary-default-hover: darken($ButtonPrimary-default-background, 10);

.ButtonPrimary {
  @include font-properties(subvisual, small);
  display: inline-flex;
  align-items: center;
  justify-content: center;

  padding: $Theme-spacing-xSmall $Theme-spacing-small;

  background-color: $ButtonPrimary-default-background;
  border: 2px solid $ButtonPrimary-default-background;
  border-radius: 8px;
  box-shadow: 0px 4px 0 rgba(0, 0, 0, 0.1);
  color: $ButtonPrimary-default-color;
  cursor: pointer;

  font-weight: $Theme-typography-fontWeight-bold;
  text-decoration: none;
  text-transform: uppercase;

  transition: all 0.3s ease-in-out;

  &:hover {
    background-color: $ButtonPrimary-default-hover;
    border-color: $ButtonPrimary-default-hover;
    color: $ButtonPrimary-default-color;
  }
}

@import '../reset';
@import '../theme_vars';

$ButtonSecondary-default-background: transparent;
$ButtonSecondary-default-color: $Theme-color-yellowOrange;
$ButtonSecondary-default-hover: $Theme-color-white;

.ButtonSecondary {
  @include font-properties(subvisual, small);
  display: inline-flex;
  align-items: center;
  justify-content: center;

  padding: $Theme-spacing-xSmall $Theme-spacing-small;

  background-color: $ButtonSecondary-default-background;
  border: 2px solid $ButtonSecondary-default-color;
  border-radius: 8px;
  color: $ButtonSecondary-default-color;
  cursor: pointer;

  font-weight: $Theme-typography-fontWeight-bold;
  text-decoration: none;
  text-transform: uppercase;

  transition: all 0.3s ease-in-out;

  &:hover {
    background-color: $ButtonSecondary-default-color;
    box-shadow: 0px 4px 0 rgba(0, 0, 0, 0.1);
    color: $ButtonSecondary-default-hover;
  }
}

@import '../reset';
@import '../theme_vars';

.Text {
  @include font-properties(bodyText, base);
  color: $Theme-palette-text-body-default;

  font-weight: $Theme-typography-fontWeight-regular;
}

.Text.Text--large {
  @include font-properties(bodyText, large);
}

.Text.Text--engagement {
  @include font-properties(heading, base);
  color: $Theme-color-white;

  font-weight: $Theme-typography-fontWeight-bold;
}

.Text.Text--tag {
  @include font-properties(subvisual, small);
  color: $Theme-color-brightTurquoise;

  text-transform: uppercase;
}

.Text.Text--light {
  color: $Theme-color-white;
}

@import '../reset';
@import '../theme_vars';

.Heading {
  @include font-properties(heading, base);
  width: 100%;

  color: $Theme-color-horizon;

  font-weight: $Theme-typography-fontWeight-bold;
}

.Heading.Heading--uvp {
  color: $Theme-color-white;

  font-weight: $Theme-typography-fontWeight-regular;
}

.Heading.Heading--small {
  @include font-properties(heading, small);
}

.Heading.Heading--brand {
  @include font-properties(subvisual, base);
  text-transform: uppercase;
}

.Heading.Heading--brandLarge {
  @include font-properties(subvisual, large);
  text-transform: uppercase;
}

.Heading-highlight {
  color: $Theme-color-brightTurquoise;
}

.Heading-light {
  color: $Theme-color-white;
}

@import '../reset';
@import '../theme_vars';

.Link {
  color: inherit;
  cursor: pointer;

  font-weight: $Theme-typography-fontWeight-bold;
  text-decoration: underline;

  &:hover {
    //TODO don't know what the hover should be
    text-decoration: underline;
  }
}

.Link.Link--highlight {
  color: $Theme-color-brightTurquoise;
}

.Media {
  display: flex;
}

.Media-image {
  padding: 0 $Theme-spacing-small;
}

.Media-content {
  flex: 1 0 0;
  display: flex;
  flex-direction: column;

  padding: 0 $Theme-spacing-small;
}

@import '../reset';
@import '../theme_vars';

.Panel {
  display: flex;
  flex-direction: column;

  width: 100%;
  overflow: hidden;

  background-color: $Theme-color-polar;
}

.Panel.Panel--alternative {
  background-color: $Theme-color-white;
}

.Panel.Panel--action {
  background-color: $Theme-color-horizon;
}

@import '../reset';
@import '../theme_vars';
@import 'font-awesome-compass';
@import 'font-awesome';

.EngagementLink {
  @include font-properties(subvisual, small);
  width: 100%;

  color: $Theme-color-yellowOrange;

  font-weight: $Theme-typography-fontWeight-bold;
  text-decoration: none;
  text-transform: uppercase;

  &:before {
    @include right-arrow();

    padding-right: $Theme-spacing-small;
  }

  &:hover {
    text-decoration: underline;
  }
}

@import '../reset';
@import '../theme_vars';

.Footer {
  display: flex;
}

.Footer-credits,
.Footer-work {
  flex: 1 0 auto;
  display: flex;
  flex-direction: column;
  justify-content: center;

  padding: $Theme-spacing-small $Theme-spacing-default;
}

.Footer-credits {
  align-items: center;

  background-color: $Theme-color-limedSpruce;
}

.Footer-work {
  display: none;
}

@include media('>=tablet') {
  .Footer-credits {
    flex-basis: 70%;
    align-items: flex-start;

    background-color: $Theme-color-limedSpruce;
  }

  .Footer-work {
    flex-basis: 30%;
    display: flex;

    background-color: $Theme-color-yellowOrange;
  }
}

.Footer-handcrafted {
  @include font-properties(subvisual, small);
  color: $Theme-color-horizon;

  text-transform: uppercase;
}

.Footer-subvisual {
  @include font-properties(subvisual, base);
  color: $Theme-color-white;

  letter-spacing: 1px;
  text-transform: uppercase;
}

.Footer-interested {
  @include font-properties(bodyText, large);
  margin-bottom: $Theme-spacing-small;

  color: $Theme-color-limedSpruce;
}

.Footer-work-text {
  @include font-properties(subvisual, small);
  color: $Theme-color-white;

  font-weight: $Theme-typography-fontWeight-bold;
  text-decoration: none;
  text-transform: uppercase;

  &:hover {
    text-decoration: underline;
  }

  &:after {
    @include right-arrow();

    padding-left: $Theme-spacing-small;
  }
}

@import '../reset';
@import '../theme_vars';

.SitemapTopic-heading {
  @include font-properties(subvisual, small);
  color: $Theme-color-horizon;

  font-weight: $Theme-typography-fontWeight-bold;
  text-transform: uppercase;
}

.SitemapTopic-links {
  @include font-properties(bodyText, small);

  margin-top: $Theme-spacing-xSmall;
}

@import '../reset';
@import '../theme_vars';

.StickyElement {
  position: fixed;
  top: 0;
  z-index: $Theme-layer-stickyElement;

  width: 100%;
}

@import '../reset';
@import '../theme_vars';

.GridCell {
  display: flex;
  justify-content: flex-start;

  width: 100%;
  padding: 0 $Theme-spacing-default;
}

.GridCell.GridCell--alignCenter {
  justify-content: center;
}

.GridCell.GridCell--alignRight {
  justify-content: flex-end;
}

.GridCell-content {
  max-width: 565px;
}

@import '../reset';
@import '../theme_vars';

$Nav-item-color-hover: $Theme-color-yellowOrange;

.Nav {
  display: flex;
  justify-content: space-between;

  width: 100%;
  height: $Theme-nav-height;
  padding: 0 $Theme-spacing-default;

  background: transparent;

  transition: background 0.2s ease-in-out;
}

.Nav.Nav--filled {
  background: $Theme-color-white;
  box-shadow: 0 4px 11px rgba(230, 230, 230, 0.4);
}

.Nav-logo {
  display: flex;

  &.is-hidden {
    display: none;
  }
}

.Nav-items {
  display: flex;
  justify-content: flex-end;
}

.Nav-item {
  display: flex;
  align-items: center;

  border-top: 5px solid transparent;
  border-bottom: 5px solid transparent;
  cursor: pointer;

  transition: border-color 0.2s ease-in-out;

  &:not(:first-child) {
    display: none;
    margin-left: $Theme-spacing-default;
  }

  &:hover,
  &.is-selected {
    border-bottom: 5px solid $Nav-item-color-hover;

    .Nav-link {
      color: $Nav-item-color-hover;
    }
  }
}

.Nav-link {
  @include font-properties(subvisual, small);
  color: $Theme-color-poloBlue;

  font-weight: $Theme-typography-fontWeight-bold;
  text-decoration: none;
  text-transform: uppercase;

  transition: color 0.2s ease-in-out;
}

.Nav.Nav--light {
  &:not(.Nav--filled) {
    .Nav-link {
      color: $Theme-color-white;
    }
  }

  .Nav-item:hover,
  .Nav-item.is-selected {
    .Nav-link {
      color: $Nav-item-color-hover;
    }
  }
}

@include media('>=tablet') {
  .Nav-item:not(:first-child) {
    display: flex;
  }
}

@import '../reset';
@import '../theme_vars';

.ContentFormatter {
  width: 100%; // flex children aren't full width by default

  text-align: left;
}

.ContentFormatter.ContentFormatter--centerHorizontally {
  text-align: center;
}

.ContentFormatter.ContentFormatter--center {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
}

@import '../reset';
@import '../theme_vars';

@mixin HorizontalGrid-frameStyles($color) {
  border-top: 2px solid $color;
  border-bottom: 2px solid $color;

  .HorizontalGrid-cell {
    border-left: 2px solid $color;
  }

  .HorizontalGrid-cell:last-child {
    border-right: 2px solid $color;
  }
}

.HorizontalGrid {
  display: flex;
  flex-direction: column;

  width: 100%;

  @include media('>=tablet') {
    flex-direction: row;
  }
}

.HorizontalGrid.HorizontalGrid--limited {
  max-width: 1280px;
  margin: 0 auto;
}

.HorizontalGrid.HorizontalGrid--lightFrame {
  @include HorizontalGrid-frameStyles($Theme-color-white);
}

.HorizontalGrid.HorizontalGrid--darkFrame {
  @include HorizontalGrid-frameStyles($Theme-color-polar);
}

.HorizontalGrid.HorizontalGrid--noTopFrame {
  border-top-width: 0;
}

.HorizontalGrid.HorizontalGrid--withGutters {
  $halfGutterSize: $Theme-spacing-small;
  padding: 0 $halfGutterSize;

  border-top-width: 0;

  .HorizontalGrid-cell {
    padding: 0 $halfGutterSize;
  }
}

.HorizontalGrid-cell {
  flex: 1;
  display: flex;
  flex-direction: column;
}

// Brand Colors
$Theme-color-brightTurquoise: #0dc0f2;
$Theme-color-horizon: #5889a2;
$Theme-color-limedSpruce: #2f3d45;
$Theme-color-polar: #ebf5fa;
$Theme-color-poloBlue: #8db6cb;
$Theme-color-white: #fff;
$Theme-color-yellowOrange: #fab23c;

$Theme-font-families: (
  subvisual: ('futura-pt', sans-serif),
  heading: ('merriweather', serif),
  bodyText: ('pt-serif', serif)
);
$Theme-font-sizes: (
  mobile: (
    // TODO: add mobile measurements
    subvisual: (
      small: (
        font-size: 16px,
        line-height: 28px
      ),
      base: (
        font-size: 24px,
        line-height: 36px
      ),
      large: (
        font-size: 40px,
        line-height: 52px
      )
    ),
    heading: (
      small: (
        font-size: 20px,
        line-height: 32px
      ),
      base: (
        font-size: 24px,
        line-height: 36px
      )
    ),
    bodyText: (
      small: (
        font-size: 16px,
        line-height: 28px
      ),
      base: (
        font-size: 20px,
        line-height: 32px
      ),
      large: (
        font-size: 24px,
        line-height: 36px
      )
    )
  ),
  desktop: (
    subvisual: (
      small: (
        font-size: 16px,
        line-height: 28px
      ),
      base: (
        font-size: 24px,
        line-height: 36px
      ),
      large: (
        font-size: 40px,
        line-height: 52px
      )
    ),
    heading: (
      small: (
        font-size: 20px,
        line-height: 32px
      ),
      base: (
        font-size: 24px,
        line-height: 36px
      )
    ),
    bodyText: (
      small: (
        font-size: 16px,
        line-height: 28px
      ),
      base: (
        font-size: 20px,
        line-height: 32px
      ),
      large: (
        font-size: 24px,
        line-height: 36px
      )
    )
  )
);

@mixin font-properties($family, $size: 'base', $font-sizes: $Theme-font-sizes) {
  font-family: map-get($Theme-font-families, $family);
  font-size: map-get(map-get(map-get(map-get($font-sizes, 'mobile'), $family), $size), 'font-size');
  line-height: map-get(map-get(map-get(map-get($font-sizes, 'mobile'), $family), $size), 'line-height');

  @include media('>=tablet') {
    font-size: map-get(map-get(map-get(map-get($font-sizes, 'desktop'), $family), $size), 'font-size');
    line-height: map-get(map-get(map-get(map-get($font-sizes, 'desktop'), $family), $size), 'line-height');
  }
}

@mixin right-arrow() {
  @include fa-icon();

  font-size: 24px; // The icon is bigger than the text of the link
  vertical-align: middle;

  content: $fa-var-arrow-circle-right;
}

.u-textAlignCenter {
  text-align: center;
}

.u-onlySmall {
  @include media('>tablet') {
    display: none;
  }
}

.u-exceptSmall {
  @include media('<=tablet') {
    display: none;
  }
}

@import '../theme_vars';

.u-smallMargin {
  margin-bottom: $Theme-spacing-small;
}

.u-defaultMargin {
  margin-bottom: $Theme-spacing-default;
}

.u-largeMargin {
  margin-bottom: $Theme-spacing-large;
}

.u-doubleLargeMargin {
  margin-bottom: $Theme-spacing-large * 2;
}

.u-navPlaceholder {
  margin-bottom: $Theme-nav-height;
}

